void SetConsoleWindow(HANDLE conout, SHORT cols, SHORT rows)
{
	CONSOLE_SCREEN_BUFFER_INFOEX sbInfoEx;
	sbInfoEx.cbSize = sizeof(CONSOLE_SCREEN_BUFFER_INFOEX);
	GetConsoleScreenBufferInfoEx(conout, &sbInfoEx);
	sbInfoEx.dwSize.X = cols;
	sbInfoEx.dwSize.Y = rows;
	sbInfoEx.srWindow = { 0, 0, cols, rows };
	sbInfoEx.dwMaximumWindowSize = { cols, rows };
	SetConsoleScreenBufferInfoEx(conout, &sbInfoEx);

	DWORD mode;
	GetConsoleMode(conout, &mode);
	mode &= ~ENABLE_WRAP_AT_EOL_OUTPUT;
	SetConsoleMode(conout, mode);

	SetConsoleTitle(L"Console Test");
}

void print_board() {
	system("CLS");

	string output = "";

	for (int i = 1; i < board_size_i + 1; i++) {
		//output = "";
		for (int j = 1; j < board_size_j + 1; j++) {
			if (board[i][j] == 0) {
				output += "\033[107m ";
				continue;
			}
			output += "\033[40m ";
		}
		output += "\n";
		//cout << output;
	}
	cout << output;

	//cout << generation;



	HANDLE out = GetStdHandle(STD_OUTPUT_HANDLE);
	SHORT cols = 301, rows = 300;
	SetConsoleWindow(out, cols, rows);
	SetConsoleCursorPosition(out, { 0,0 });
	
}

int nr_of_neighbours(int x, int y) {
	int s = 0;
	for (int i = x + 1; i > x - 2; i--) {
		for (int j = y - 1; j < y + 2; j++) {
			if(board[i][j] == 1 || board[i][j] == 2 || board[i][j] == 3)
				s++;
		}
	}
	return s - board[x][y];
}

void tick() {
	for (int i = 1; i < board_size_i + 1; i++) {
		for (int j = 1; j < board_size_j + 1; j++) {
			if (board[i][j] == 0) {
				if (nr_of_neighbours(i, j) == 3)
					board[i][j] = 4;
				continue;
			}

			switch (nr_of_neighbours(i, j)) {
				case(0):
				case(1):
					board[i][j] = 2;
					break;
				case(2):
				case(3):
					board[i][j] = 3;
					break;

				default:
					board[i][j] = 2;
					break;
			}
		}
	}

	for (int i = 1; i < board_size_i + 1; i++) {
		for (int j = 1; j < board_size_j + 1; j++) {
			if (board[i][j] == 0)
				continue;

			board[i][j] -= 2;
			if (board[i][j] == 2)
				board[i][j]--;
		}
	}

	generation++;
}

void init_board() {
	for (int i = offset; i < init_board_size_i+offset; i++) {
		for (int j = 0; j < init_board_size_j; j++) {
			board[i + 1][j + 1] = new_board[i-offset][j];
		}
	}
}

int main() {

	printf("\033[107m");

	init_board();
	print_board();

	int z = 0;

	while (true) {
		tick();
		Sleep(sleep_duration);
		if (z == step) {
			print_board();
			z = 0;
		}
		z++;
	}

}