Hexxy::Display* display;

void print_board() {

	for (int i = 1; i < board_size_i + 1; i++) {
		for (int j = 1; j < board_size_j + 1; j++) {
			display->Buffer[i][j] = board[i][j];
		}
	}
}

int nr_of_neighbours(int x, int y) {
	int s = 0;
	for (int i = x + 1; i > x - 2; i--) {
		for (int j = y - 1; j < y + 2; j++) {
			if(board[i][j] == 1 || board[i][j] == 2 || board[i][j] == 3)
				s++;
		}
	}
	return s - board[x][y];
}

void tick() {
	for (int i = 1; i < board_size_i + 1; i++) {
		for (int j = 1; j < board_size_j + 1; j++) {
			if (board[i][j] == 0) {
				if (nr_of_neighbours(i, j) == 3)
					board[i][j] = 4;
				continue;
			}

			switch (nr_of_neighbours(i, j)) {
				case(0):
				case(1):
					board[i][j] = 2;
					break;
				case(2):
				case(3):
					board[i][j] = 3;
					break;

				default:
					board[i][j] = 2;
					break;
			}
		}
	}

	for (int i = 1; i < board_size_i + 1; i++) {
		for (int j = 1; j < board_size_j + 1; j++) {
			if (board[i][j] == 0)
				continue;

			board[i][j] -= 2;
			if (board[i][j] == 2)
				board[i][j]--;
		}
	}

	generation++;
}

void init_board() {
	for (int i = offset; i < init_board_size_i+offset; i++) {
		for (int j = 0; j < init_board_size_j; j++) {
			board[i + 1][j + 1] = new_board[i-offset][j];
		}
	}
}

int main() {
	Hexxy::Display display(board_size_i + 1, board_size_j + 1, 5);
	::display = &display;

	display.ColorPalette.SetClearColor(0, 255, 255);

	display.ColorPalette.set(0, 255, 255, 255);
	display.ColorPalette.set(1, 0, 0, 0);

	display.UpdateColors();

	init_board();
	print_board();

	int z = 0;

	while (!display.WindowShouldClose()) {
		tick();
		Sleep(sleep_duration);
		if (z == step) {
			print_board();
			z = 0;
		}
		z++;
		display.Draw();
	}

}